mapper.select:
select * from (
	select rownum r_num, a.* from (
		select * from gw_email where sender_addr = #{email_addr} or email_addr = #{email_addr} order by regdate desc
	) a
) where r_num between #{begin} and #{end}

select * from (
	select rownum r_num, a.* from (
		select * from gw_email where email_addr = #{email_addr} order by regdate desc
	) a
) where r_num between #{begin} and #{end}

select * from (
	select rownum r_num, a.* from (
		select * from gw_email where sender_addr = #{email_addr} order by regdate desc
	) a
) where r_num between #{begin} and #{end}

select * from (
	select rownum r_num, a.* from (
		select * from gw_email where sender_addr = #{email_addr} and email_addr = #{email_addr} order by regdate desc
	) a
) where r_num between #{begin} and #{end}

select * from ( 
	select rownum r_num, a.* from ( 
		select * from 
			(select * from gw_email where email_addr = '${email_addr}' or sender_addr = '${email_addr}') 
				where email_addr like '%${search}%' or sender_addr like '%${search}%' or subject like '%${search}%' order by regdate desc 
	) a 
) where r_num between #{begin} and #{end}

		select * from gw_board where board_type = #{board_type}
			select * from 
				(select rownum r_num, a.* from 
            		(select * from gw_board where board_type = #{board_type} order by b_idx) a) 
              where r_num between #{begin} and #{end}
